  private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    // TODO add your handling code here:
    Integer numero;
    String numeroString;

    numeroString = edit1.getText();

    if (numeroString.isEmpty()) {
      JOptionPane.showMessageDialog(null, "Digite um valor");
    } else {
      numero = Integer.parseInt(edit1.getText());
      if (numero == 1) {
        labresult.setText("Janeiro");
      } else if (numero == 2) {
        labresult.setText("Fevereiro");
      } else if (numero == 3) {
        labresult.setText("Marco");
      } else if (numero == 4) {
        labresult.setText("Abril");
      } else if (numero == 5) {
        labresult.setText("Maio");
      } else if (numero == 6) {
        labresult.setText("Junho");
      } else if (numero == 7) {
        labresult.setText("Julho");
      } else if (numero == 8) {
        labresult.setText("Agosto");
      } else if (numero == 9) {
        labresult.setText("Setembro");
      } else if (numero == 10) {
        labresult.setText("Outubro");
      } else if (numero == 11) {
        labresult.setText("Novembro");
      } else if (numero == 12) {
        labresult.setText("Dezembro");
      } else {
        labresult.setText("Nao existe mes com esse numero");
      }
    }
  }//GEN-LAST:event_jButton1ActionPerformed

