  private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    // TODO add your handling code here:
    Integer numero;
    String numeroString;

    numeroString = edit1.getText();

    if (numeroString.isEmpty()) {
      JOptionPane.showMessageDialog(null, "Digite um valor");
    } else {
      numero = Integer.parseInt(edit1.getText());
      switch (numero) {
        case 1 -> labresult.setText("Janeiro");
        case 2 -> labresult.setText("Fevereiro");
        case 3 -> labresult.setText("Marco");
        case 4 -> labresult.setText("Abril");
        case 5 -> labresult.setText("Maio");
        case 6 -> labresult.setText("Junho");
        case 7 -> labresult.setText("Julho");
        case 8 -> labresult.setText("Agosto");
        case 9 -> labresult.setText("Setembro");
        case 10 -> labresult.setText("Outubro");
        case 11 -> labresult.setText("Novembro");
        case 12 -> labresult.setText("Dezembro");
        default -> labresult.setText("Nao existe mes com esse numero");
      }
    }
  }//GEN-LAST:event_jButton1ActionPerformed

